import React from 'react';
import './style.Contact.css';
class Contact extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = {
        name: '',
        email: '',
        message: '',
        }          
        this.handleClick = this.handleClick.bind(this);
    }
    handleFormSubmit( event ) {
    event.preventDefault();
    console.log(this.state);
    }
    render(){
        return(
            <div className="Contact container-fluid">
                <div className="d-flex row justify-content-around my-3">
                    <div className="col flex-container1">
                        <span className="googlehoverp s2">Contact Me</span>
                    </div>
                </div>
                <div className="d-flex row justify-content-around my-3">
                    <div className="col flex-container1">
                        <span className="">
                            <i class="fas fa-envelope xy m-3"></i>
                        </span>
                        <span className="row s3">
                            ekthapriyesh24@gmail.com
                        </span>
                    </div>
                    <div className="col flex-container1">
                        <span className="">
                        <i class="fas fa-map-marker-alt xy m-3"></i>
                        </span>
                        <span className="row s3">
                            Allahabad-211012
                        </span>
                    </div>
                    <div className="col flex-container1">
                        <span className="">
                        <i class="fas fa-phone-alt xy m-3"></i>
                        </span>
                        <span className="row s3">
                        +91-**********
                        </span>
                    </div>
                </div>
                <form action="/contactform" method="post" className="jumbotron bg-light container googlehoverp">
                    <div className="d-flex row justify-content-between m-1">
                        <input placeholder="Your Name" id="name" type="text" value={this.state.name} className="w-25" onChange={e => this.setState({ fname: e.target.value })}
/>
                        <input placeholder="Your e-mail" id="email" type="text" value={this.state.email} name="email" className="w-50" onChange={e => this.setState({ fname: e.target.value })}
/>
                    </div>
                    <div className="form-group d-flex row justify-content-around m-1">
                        <textarea rows="4" columns="9" className="w-100 my-3" id="message" placeholder="Let's start the conversation" value={this.state.message} onChange={e => this.setState({ fname: e.target.value })}
></textarea>
                    </div>
                    <div className="form-group d-flex row justify-content-around m-1">
                        <button className="btn btn-outline-success" type="submit">Send</button>
                    </div>
                </form>
                <div className="bg-dark d-flex row justify-content-around p-1">
                    <span className="text-light s3">created with <i class="fas fa-heart he xy1"></i> by Priyesh Raj <i class="far fa-copyright xy1"></i>2019</span>
                </div>
            </div>
        );
    }
}
export default Contact;
